$(document).ready(function () { function highlightNames() { const colorsOfNames = { "Charlotte Goupil": "#741D34", "Fabienne Bergès": "#1D7461" }; const mainContent = $("#mentionsLegales, #politiqueConfidentialite"); let content = mainContent.html(); for (const [name, color] of Object.entries(colorsOfNames)) { const regex = new RegExp(name, "g"); content = content.replace(regex, `<span style="color: ${color}; font-weight: bold">${name}</span>`) } mainContent.html(content) } if (window.location.pathname === "/Multiples_Charlotte/MentionsLegales.php" || window.location.pathname === "/Multiples_Charlotte/PolitiqueConfidentialite.php") { highlightNames() } }), $(".not-authorized-overlay").hide(); $("#Bulles .photo").hover(function () { $(this).find(".not-authorized-overlay").stop(true, true).fadeIn(300) }, function () { $(this).find(".not-authorized-overlay").stop(true, true).fadeOut(100) }); $(function () { $(".navbar a").on("click", function (t) { t.preventDefault(); var o = this.hash; $("body,html").animate({ scrollTop: $(o).offset().top }, 850, function () { window.location.hash = o }) }); $("#myNavbar a").on("click", function () { $("#myNavbar").collapse("hide") }); function validateField($field, validationFunction) { const value = $field.val().trim(); const $errorMessage = $field.next(".comments"); if (validationFunction(value)) { $errorMessage.empty(); return true } else { return false } } $("#firstname, #name").on("input", function () { validateField($(this), value => value !== "" && validateNameAndFirstname(value)) }); $("#email").on("input", function () { validateField($(this), validateEmail) }); $("#phone").on("input", function () { validateField($(this), validatePhone) }); $("#subject").on("change", function () { validateField($(this), value => value !== "") }); $("#message").on("input", function () { validateField($(this), value => value !== "") }); $("#data-consent").on("change", function () { validateField($(this), value => $(this).is(":checked")) }); $("#contact-form").submit(function (event) { event.preventDefault(); $(".comments").empty(); let isValid = true; isValid = validateField($("#firstname"), value => value !== "" && validateNameAndFirstname(value)) && isValid; isValid = validateField($("#name"), value => value !== "" && validateNameAndFirstname(value)) && isValid; isValid = validateField($("#email"), validateEmail) && isValid; isValid = validateField($("#phone"), validatePhone) && isValid; isValid = validateField($("#subject"), value => value !== "") && isValid; isValid = validateField($("#message"), value => value !== "") && isValid; isValid = validateField($("#data-consent"), value => $("#data-consent").is(":checked")) && isValid; if (!$("#firstname").val().trim()) { $("#firstname").next(".comments").html("Merci de m'indiquer votre prénom") } else if (!validateNameAndFirstname($("#firstname").val().trim())) { $("#firstname").next(".comments").html("Le prénom ne doit pas contenir de chiffres") } if (!$("#name").val().trim()) { $("#name").next(".comments").html("Votre nom est aussi nécessaire") } else if (!validateNameAndFirstname($("#name").val().trim())) { $("#name").next(".comments").html("Le nom ne doit pas contenir de chiffres") } if (!validateEmail($("#email").val())) { $("#email").next(".comments").html("Votre email n'est pas valide") } if (!validatePhone($("#phone").val())) { $("#phone").next(".comments").html("Seuls les chiffres sont utilisables.") } if (!$("#subject").val().trim()) { $("#subject").next(".comments").html("Désolé, vous avez oublié d'indiquer le sujet de votre message") } if (!$("#message").val().trim()) { $("#message").next(".comments").html("Vous avez oublié d'écrire votre message") } if (!$("#data-consent").is(":checked")) { $("#data-consent").next(".comments").html("Vous devez accepter les termes et conditions") } if (isValid) { let formData = $("#contact-form").serialize(); $.ajax({ type: "POST", url: "php/contact.php", data: formData, dataType: "json", success: function (response) { if (response.isSuccess) { $("#contact-form").append("<p class='thank-you'>Votre message a bien été envoyé. Merci de m'avoir contacté :)</p>"); $("#contact-form")[0].reset(); $("#contact-form .thank-you").delay(3e3).fadeOut("slow") } else { $("#firstname").next(".comments").html(response.firstnameError); $("#name").next(".comments").html(response.nameError); $("#email").next(".comments").html(response.emailError); $("#phone").next(".comments").html(response.phoneError); $("#subject").next(".comments").html(response.subjectError); $("#message").next(".comments").html(response.messageError); $("#data-consent").next(".comments").html(response.consentError) } } }) } }); function validateNameAndFirstname(value) { const nameRegex = /^[a-zA-ZÀ-ÿ\s'-]*$/; return nameRegex.test(value) } function validateEmail(email) { const nameRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; return nameRegex.test(email) } function validatePhone(phone) { const nameRegex = /^[0-9 ]*$/; return nameRegex.test(phone) } });